        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            produits: []
        };
    }

    componentDidMount() {
        fetch("http://localhost:8888/Projet/api-rest/produits/lire.php")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        produits: result.produits
